mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.create_time, d.update_time, d.create_user, d.update_user, c.name AS category_name from dish AS d join category AS c on d.category_id = c.id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by create_time desc
